import React, { Component } from 'react';
import { Box, Container, TextField, Button } from '@material-ui/core';

class RevealBidComponent extends React.Component {

    state = {
        nonce: 0
    }

    componentDidMount() {

    }

    onChange= (e) => this.setState({[e.target.name]: e.target.value});

    render() {
        return(
            <div className="RevealBidComponent">
                <Box py={6} px={10}>
                    <h3>Submit your nonce</h3>
                    <Container>
                        <form>
                            <div>
                                <div className="co1-1">
                                    <TextField
                                        label="Enter nonce"
                                        margin="normal"
                                        variant="outlined"
                                        value={this.state.nonce}
                                        onChange={this.onChange}
                                    />
                                </div>
                                <div className="col-2">
                                <Button variant="contained"
                                        color="secondary"
                                        type="submit"
                                        value="submit" >
                                    Send
                                </Button>
                                </div>
                            </div>
                        </form>
                    </Container>
                </Box>
            </div>
        )
    }
}

export default RevealBidComponent;
